import { Component } from '@angular/core';
import { Usuario } from 'projects/github-user-widget/src/lib/interfaces/usuario.interface';
import { USUARIOAPI } from 'projects/github-user-widget/src/lib/constants/usuario';

@Component({
  selector: 'guw-usuario',
  templateUrl: './usuario.component.html',
  styleUrls: ['./usuario.component.css']
})
export class UsuarioComponent {
  usuario: Usuario;
  constructor() {
    this.usuario = USUARIOAPI;
  }
}
<div class="github-card user-card">
    <div class="header user"></div>
    <a class="avatar" [href]="'https://github.com/' + usuario.login">
        <img [src]="usuario.avatar_url + '&s=80'" />
    </a>
    <div class="content">
        <ul class="status">
            <li>
                <a [href]="'https://github.com/' + usuario.login">
                    <strong>{{ usuario.public_repos }}</strong>Repositorios
                </a>
            </li>
        </ul>
    </div>
    <div>
        <p>{{ usuario.login }}</p>
        <p>{{ usuario.id }}</p>
        <p>{{ usuario.url }}</p>
    </div>
</div>
