import { Component, Input } from '@angular/core';
import { Usuario } from 'projects/github-user-widget/src/lib/interfaces/usuario.interface';
import { USUARIOAPI } from 'projects/github-user-widget/src/lib/constants/usuario';
import { BuscadorUsuarioService } from 'projects/github-user-widget/src/lib/services/buscador-usuario.service';
@Component({
  selector: 'guw-usuario',
  templateUrl: './usuario.component.html',
  styleUrls: ['./usuario.component.css']
})
export class UsuarioComponent {
  @Input() busqueda: string;
  usuario: Usuario | null;
  constructor(private readonly buscadorUsuarioService: BuscadorUsuarioService) {
    this.busqueda = '';
    this.usuario = null;
    if(this.busqueda === undefined || this.busqueda === null || this.busqueda === ''){
      this.usuario = USUARIOAPI;
    }
    this.buscadorUsuarioService.obtenerDatosUsuario(this.busqueda).then(
      (data: any) => {
        this.usuario = data;
      }
    );
  }
}
<div class="github-card user-card" *ngIf="usuario">
    <div class="header user"></div>
    <a class="avatar" [href]="'https://github.com/' + usuario.login">
        <img [src]="usuario.avatar_url + '&s=80'" />
    </a>
    <div class="content">
        <ul class="status">
            <li>
                <a [href]="'https://github.com/' + usuario.login">
                    <strong>{{ usuario.public_repos }}</strong>Repositorios
                </a>
            </li>
            <li>
                <a [href]="'https://github.com/' + usuario.login + '?tab=followers'">
                    <strong>{{ usuario.followers }}</strong>Seguidores
                </a>
            </li>
            <li>
                <a [href]="'https://github.com/' + usuario.login">
                    <strong>{{ usuario.public_gists }}</strong>Gists
                </a>
            </li>
        </ul>
    </div>
    <div>
        <p>{{ usuario.login }}</p>
        <p>{{ usuario.id }}</p>
        <p>{{ usuario.url }}</p>
    </div>
</div>
